mapper.select:
SELECT UI_NUM, UI_NAME, UI_ID, UI_PWD 
FROM USER_INFO


		AND UI_NAME LIKE CONCAT('%',#{uiName},'%')


		AND UI_NUM = #{uiNum}


		AND UI_ID = #{uiId}

SELECT UI_NUM, UI_NAME, UI_ID, UI_PWD 
FROM USER_INFO
WHERE UI_NUM = #{uiNum}

SELECT UI_NUM, UI_NAME, UI_ID, UI_PWD 
FROM USER_INFO
WHERE UI_ID = #{uiId}
AND UI_PWD = #{uiPwd}

mapper.update:
UPDATE USER_INFO
SET UI_NAME=#{uiName},
UI_PWD=#{uiPwd}
WHERE UI_NUM=#{uiNum}
